) v_cucm_phone_line ON CUCM_PHONE.CUCM_PHONE_ID = V_CUCM_PHONE_LINE.cucmtelIdInLine LEFT OUTER JOIN (
SELECT APP_USER.APP_USER_ID AS userId, APP_USER.NAME_KANJI AS kanjiUserName, APP_USER.NAME_KANA AS kanaUserName, APP_USER.ENABLED_SHARED_USE AS sharedUse, APP_USER.CUCM_UPDATE_REQUEST_FLAG AS userUpdateRequest, APP_USER.ERROR_FLG AS userErrorFlg, R_USER_SECTION.COMPANY_ID AS companyId, R_USER_SECTION.SECTION_ID AS sectionId, R_USER_SECTION.SECTION_NAME AS sectionName, R_USER_SECTION.PRINT_ORDER AS userSectionPrintorder, R_CUCM_USER_PHONE.CUCM_PHONE_ID AS cucmtelIdInUser, R_CUCM_USER_PHONE.CUCM_UPDATE_REQUEST_FLAG AS userPhoneUpdateRequest, R_CUCM_USER_PHONE.ERROR_FLG AS userPhoneErrorFlg, T_SECTION_CHILD.PRINT_ORDER AS childPrintorder, T_SECTION_CHILD.SECTION_NAME AS childSectionName, T_SECTION_CHILD.PRINT_ORDER AS parentPrintorder FROM R_CUCM_USER_PHONE INNER JOIN APP_USER ON R_CUCM_USER_PHONE.APP_USER_ID = APP_USER.APP_USER_ID AND R_CUCM_USER_PHONE.DELETED = #{COM_FLG_OFF} INNER JOIN R_USER_SECTION ON APP_USER.APP_USER_ID = R_USER_SECTION.APP_USER_ID AND R_CUCM_USER_PHONE.COMPANY_ID = R_USER_SECTION.COMPANY_ID AND R_CUCM_USER_PHONE.SECTION_ID = R_USER_SECTION.SECTION_ID AND R_USER_SECTION.DELETED = #{COM_FLG_OFF} INNER JOIN M_SECTION T_SECTION_CHILD ON R_USER_SECTION.SECTION_ID = T_SECTION_CHILD.SECTION_ID AND R_USER_SECTION.COMPANY_ID = T_SECTION_CHILD.COMPANY_ID INNER JOIN M_SECTION T_SECTION_PARENT ON T_SECTION_CHILD.PARENT_SECTION_ID = T_SECTION_PARENT.SECTION_ID WHERE APP_USER.DELETED = #{COM_FLG_OFF} ) v_cucm_user_phone ON CUCM_PHONE.CUCM_PHONE_ID = v_cucm_user_phone.cucmtelidinuser LEFT OUTER JOIN TEL_DIR_ASSOCIATION ON v_cucm_phone_line.lineId = TEL_DIR_ASSOCIATION.CUCM_LINE_ID AND CUCM_PHONE.CUCM_PHONE_ID = TEL_DIR_ASSOCIATION.CUCM_PHONE_ID AND v_cucm_user_phone.userId = TEL_DIR_ASSOCIATION.APP_USER_ID LEFT OUTER JOIN UNITY_ASSOCIATION ON CUCM_PHONE.CUCM_PHONE_ID = UNITY_ASSOCIATION.CUCM_PHONE_ID INNER JOIN M_BRANCH ON CUCM_PHONE.BRANCH_ID = M_BRANCH.BRANCH_ID INNER JOIN M_CLUSTER ON M_BRANCH.CLUSTER_ID = M_CLUSTER.CLUSTER_ID INNER JOIN M_SECTION T_SECTION_CHILD ON CUCM_PHONE.SECTION_ID = T_SECTION_CHILD.SECTION_ID AND CUCM_PHONE.COMPANY_ID = T_SECTION_CHILD.COMPANY_ID INNER JOIN M_SECTION T_SECTION_PARENT ON T_SECTION_CHILD.PARENT_SECTION_ID = T_SECTION_PARENT.SECTION_ID
LEFT OUTER JOIN PICKUP_GROUP ON V_CUCM_PHONE_LINE.PICKUPGROUPNAME = PICKUP_GROUP.NAME AND M_CLUSTER.CLUSTER_ID = PICKUP_GROUP.CLUSTER_ID WHERE cucm_phone.deleted =#{COM_FLG_OFF} UNION ALL
