v_user_donthave_phone.userUpdateRequest = #{CUCM_UPDATE_FLG_WAIT} THEN #{REFLECTION_WAIT} ELSE #{REFLECTION_FINISHED} END AS statusCode , CASE WHEN v_user_donthave_phone.userUpdateRequest = #{CUCM_UPDATE_FLG_WAIT_2} THEN #{REFLECTION_WAIT_NAME} WHEN v_user_donthave_phone.userErrorFlg = #{COM_FLG_ON} THEN #{REFLECTION_ERROR_NAME} WHEN v_user_donthave_phone.userUpdateRequest = #{CUCM_UPDATE_FLG_WAIT_3} THEN #{REFLECTION_BATCH_NAME} WHEN v_user_donthave_phone.userUpdateRequest = #{CUCM_UPDATE_FLG_WAIT} THEN #{REFLECTION_WAIT_NAME} ELSE #{REFLECTION_FINISHED_NAME} END AS status FROM( SELECT APP_USER.APP_USER_ID AS userId, APP_USER.NAME_KANJI AS kanjiUserName, APP_USER.NAME_KANA AS kanaUserName, R_USER_SECTION.COMPANY_ID AS companyId, R_USER_SECTION.SECTION_ID AS sectionId, R_USER_SECTION.SECTION_NAME AS sectionName, R_USER_SECTION.PRINT_ORDER AS userSectionPrintorder, APP_USER.ENABLED_SHARED_USE AS enabledSharedUse, APP_USER.CUCM_UPDATE_REQUEST_FLAG AS userUpdateRequest, APP_USER.ERROR_FLG AS userErrorFlg, T_SECTION_PARENT.PRINT_ORDER AS userSectionparentPrintorder, T_SECTION_CHILD.PRINT_ORDER AS userSectionchildPrintorder, T_SECTION_CHILD.SECTION_NAME AS childSectionName, T_SECTION_CHILD.ORG_ID AS orgId FROM APP_USER INNER JOIN R_USER_SECTION ON APP_USER.APP_USER_ID = R_USER_SECTION.APP_USER_ID AND R_USER_SECTION.DELETED = #{COM_FLG_OFF} INNER JOIN M_SECTION T_SECTION_CHILD ON R_USER_SECTION.COMPANY_ID = T_SECTION_CHILD.COMPANY_ID AND R_USER_SECTION.SECTION_ID = T_SECTION_CHILD.SECTION_ID INNER JOIN M_SECTION T_SECTION_PARENT ON T_SECTION_CHILD.PARENT_SECTION_ID = T_SECTION_PARENT.SECTION_ID WHERE APP_USER.DELETED = #{COM_FLG_OFF}
EXCEPT
SELECT APP_USER.APP_USER_ID AS userId, APP_USER.NAME_KANJI AS kanjiUserName, APP_USER.NAME_KANA AS kanaUserName, R_USER_SECTION.COMPANY_ID AS companyId, R_USER_SECTION.SECTION_ID AS sectionId, R_USER_SECTION.SECTION_NAME AS sectionName, R_USER_SECTION.PRINT_ORDER AS userSectionPrintorder, APP_USER.ENABLED_SHARED_USE AS enabledSharedUse, APP_USER.CUCM_UPDATE_REQUEST_FLAG AS userUpdateRequest, APP_USER.ERROR_FLG AS userErrorFlg, T_SECTION_PARENT.PRINT_ORDER AS userSectionparentPrintorder, T_SECTION_CHILD.PRINT_ORDER AS userSectionchildPrintorder, T_SECTION_CHILD.SECTION_NAME AS childSectionName, T_SECTION_CHILD.ORG_ID AS orgId FROM R_CUCM_USER_PHONE INNER JOIN CUCM_PHONE ON R_CUCM_USER_PHONE.CUCM_PHONE_ID = CUCM_PHONE.CUCM_PHONE_ID AND R_CUCM_USER_PHONE.DELETED = #{COM_FLG_OFF} INNER JOIN APP_USER ON R_CUCM_USER_PHONE.APP_USER_ID = APP_USER.APP_USER_ID AND APP_USER.DELETED = #{COM_FLG_OFF} INNER JOIN R_USER_SECTION ON R_CUCM_USER_PHONE.APP_USER_ID = R_USER_SECTION.APP_USER_ID AND R_CUCM_USER_PHONE.COMPANY_ID = R_USER_SECTION.COMPANY_ID AND R_CUCM_USER_PHONE.SECTION_ID = R_USER_SECTION.SECTION_ID AND R_USER_SECTION.DELETED = #{COM_FLG_OFF} INNER JOIN M_SECTION T_SECTION_CHILD ON R_USER_SECTION.COMPANY_ID = T_SECTION_CHILD.COMPANY_ID AND R_USER_SECTION.SECTION_ID = T_SECTION_CHILD.SECTION_ID INNER JOIN M_SECTION T_SECTION_PARENT ON T_SECTION_CHILD.PARENT_SECTION_ID = T_SECTION_PARENT.SECTION_ID ) v_user_donthave_phone ) v_tel_user_info
